namespace HackerRank.Algorithms.Tests;

public class CyclicBinaryString
{

    [Fact]
    public void MaxPower_GivenMultiSet_ShouldReturnExpectedResults()
    {
        Algorithms.CyclicBinaryString.maximumPower("0011").Should().Be(2);
        Algorithms.CyclicBinaryString.maximumPower("111").Should().Be(0);
        Algorithms.CyclicBinaryString.maximumPower("1").Should().Be(0);
        Algorithms.CyclicBinaryString.maximumPower("000").Should().Be(-1);
        Algorithms.CyclicBinaryString.maximumPower("")
            .Should().Be(10552);
    }
}
