namespace LeetCode.Algorithms.Tests;

public class Permutation_in_String
{
    [Theory]
    [InlineData("ab", "eidbaooo", true)]
    [InlineData("ab", "eidboaoo", false)]
    [InlineData("adc", "dcda", true)]
    [InlineData(""
        , "",
        true)]
    public void CheckInclusion(string s1, string s2, bool res)
    {
        Algorithms.Permutation_in_String.CheckInclusion(s1, s2).Should().Be(res);
    }
}
